mapper.select:
SELECT id AS userId
FROM User
WHERE id = #{userId} and pwd = #{pwd}

mapper.update:
UPDATE User
name = #{name}, gender = #{gender}, age = #{age},
WHERE id = #{userId}

UPDATE User
SET pwd = #{pwd}
WHERE id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM User
WHERE id = #{userId}

SELECT id AS userId, pwd, name, gender, age
FROM User
WHERE id = #{userId}

select id AS userId, warning
from User
where id = #{userId}

mapper.update:
UPDATE User
SET warning = COALESCE(warning, 0) + 1
WHERE name = #{name}
